mapper.insert:

	SELECT  'OD' || LPAD(OD_SEQ.nextval, '7', '0') ORDER_NUMBER
	from dual

INSERT INTO logistics_order(
		ORDER_NUMBER
		,ORDER_DAY
		,LOGISTICS_ID
		,CLIENT_ID)
VALUES(
		#{orderNumber}
		,SYSDATE
		,#{logisticsId}
		,#{clientId}
		)

INSERT INTO logistics_order_details
            (
			DETAILS_ORDER_NUMBER
			,COUNT
			,UNIT
			,MATERIAL_NUMBER
			,ORDER_NUMBER
			,PERSON_ID
			,IN_OPERATION
			,ORDER_COST
			,ORDER_CONDITION
			)
	VALUES (
			 'DOD' || LPAD(DOD_SEQ.nextval, '7', '0')
			,#{count}
			,#{unit}
			,#{materialNumber}
			,#{orderNumber}
			,#{personId}
			,'f1'
			,#{orderCost}
			,'c1'			
			)
